package com.Excercises;

public class ArraySum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]={5,6,8,9};
		int sum=0;
		for(int ele:a){
			sum +=ele;//sum++=5=1,6=1+1,8=2+1.....
			//mane hokouta element ase seta dibe, but sum+=element mane puratar summetion
			
		}
		System.out.println(sum);

	}

}
